import { useState } from "react";
import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";

const DUMMY_EXPENSES = [
  { id: "e1", title: "Toilet Paper", amount: 94.12, date: new Date(2020, 7, 14) },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  { id: "e3", title: "Car Insurance", amount: 294.67, date: new Date(2021, 2, 28) },
  { id: "e4", title: "New Desk (Wooden)", amount: 450, date: new Date(2021, 5, 12) },
  { id: "e5", title: "Groceries", amount: 125.36, date: new Date(2019, 1, 5) },
  { id: "e6", title: "Restaurant Dinner", amount: 65.82, date: new Date(2019, 3, 20) },
  { id: "e7", title: "Movie Tickets", amount: 27.5, date: new Date(2019, 6, 10) },
  { id: "e8", title: "Gasoline", amount: 50.21, date: new Date(2020, 9, 3) },
  { id: "e9", title: "Birthday Gift", amount: 35.0, date: new Date(2020, 10, 15) },
  { id: "e10", title: "Electronics", amount: 320.75, date: new Date(2020, 11, 28) },
  { id: "e11", title: "Home Repairs", amount: 175.0, date: new Date(2021, 1, 7) },
  { id: "e12", title: "Vacation", amount: 1200.0, date: new Date(2022, 4, 18) },
  { id: "e13", title: "Medical Expenses", amount: 210.15, date: new Date(2022, 8, 22) },
  { id: "e14", title: "Fitness Membership", amount: 85.0, date: new Date(2023, 0, 5) },
  { id: "e15", title: "Clothing", amount: 62.45, date: new Date(2023, 3, 14) },
  { id: "e16", title: "Books", amount: 28.98, date: new Date(2023, 6, 29) },
];


function App() {
  const [expenses, setExpenses] = useState(DUMMY_EXPENSES);

  const addExpenseHandler = (expense) => {
    //setExpenses([expense, ...expenses]); //Spread operator for the initial array expenses. Standard JS feature
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses]; //prevExpenses provided by react, clean way of getting the data correctly
    });
  };


  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} />
      <Expenses expenses={expenses} />
    </div>
  );
}

export default App;
