  const [disc, setDisc] = useState(100);
  const handleClick = () => {
    setDisc(75);
    console.log(disc);
  };
  return (
    <div>
      <p>${disc}</p>
      <button onClick={handleClick}>Apply Discount</button>
    </div>
  );
}
