import './styles.css';

// don't change the Component name "App"
// important: In this code editor, use React.useState() instead of just useState()
export default function App() {
        const val = 100
        const [disc, setDisc] = useState(val);
        const handleClick = () => {
          setDisc(75);
        }
    return (
        <div>
            <p>${val}</p>
            <button onClick = {handleClick} >Apply Discount</button>
        </div>
    );
}
